logical function has_close_elements(a, n, expected, tolerance)
  implicit none
  integer, intent(in) :: n
  double precision, intent(in) :: a(n), expected, tolerance
  integer :: i
  has_close_elements = .false.
  do i = 1, n
    if (abs(a(i) - expected) > tolerance) then
